describe('Directive: fis.bootstrap.accordion accordion', function () {
  'use strict';
  var templateUrl;
  var template;

  beforeEach(function () {
    module('fis.bootstrap.components');

    angular.mock.inject(function ($rootScope, $templateCache, uibAccordionDirective) {
      template = $templateCache.get('accordion/accordionGroup.decorator.html');
      templateUrl = uibAccordionDirective[0].templateUrl;
    });
  });

  it('should have found template path in $templateCache', function () {
    expect(template).toBeDefined();
  });

  it('should validate accordion decorated property', function () {
    expect(templateUrl).toBe('accordion/accordion.decorator.html');
  });
});
