describe('Directive: fis.bootstrap.calendar datepicker', function () {
  'use strict';

  var htmlelement = null;
  var template;
  var templateUrl;

  beforeEach(function () {
    angular.mock.module('fis.bootstrap.components');
    angular.mock.inject(function ($rootScope, $templateCache, uibDatepickerDirective) {
      htmlelement = '<div ng-switch="datepickerMode" role="application" ng-keydown="keydown($event)">\n  <uib-daypicker ng-switch-when="day" tabindex="0"></uib-daypicker>\n  <uib-monthpicker ng-switch-when="month" tabindex="0"></uib-monthpicker>\n  <uib-yearpicker ng-switch-when="year" tabindex="0"></uib-yearpicker>\n</div>';

      template = $templateCache.get('calendar/datepicker.decorator.html');
      templateUrl = uibDatepickerDirective[0].templateUrl;
    });
  });

  it('should match the retreived datepicker template and sample html markup', function () {
    expect(template).toBe(htmlelement);
  });

  it('should validate datepicker  decorated property', function () {
    expect(templateUrl).toBe('calendar/datepicker.decorator.html');
  });
});
