describe('Directive: fis.bootstrap.calendar monthpicker', function () {
  'use strict';
  var htmlelement;
  var template;
  var templateUrl;

  beforeEach(function () {
    angular.mock.module('fis.bootstrap.components');
    angular.mock.inject(function ($rootScope, $templateCache, uibMonthpickerDirective) {
      template = $templateCache.get('calendar/monthpicker.decorator.html');
      templateUrl = uibMonthpickerDirective[0].templateUrl;

      htmlelement = '<table role="grid" aria-labelledby="{{uniqueId}}-title" aria-activedescendant="{{activeDateId}}">\n  <thead>\n    <tr>\n      <th><button type="button" class="fis-bs-calendar-btn btn-default btn-sm fis-bs-calendar-pull-left" ng-click="move(-1)" tabindex="-1"><i class="glyphicon glyphicon-chevron-left"></i></button></th>\n      <th><button id="{{uniqueId}}-title" role="heading" aria-live="assertive" aria-atomic="true" type="button" class="fis-bs-calendar-midle btn-default btn-sm" ng-click="toggleMode()" tabindex="-1" style="width:100%;"><strong>{{title}}</strong></button></th>\n      <th><button type="button" class="fis-bs-calendar-btn btn-default btn-sm fis-bs-calendar-pull-right " ng-click="move(1)" tabindex="-1"><i class="glyphicon glyphicon-chevron-right"></i></button></th>\n    </tr>\n  </thead>\n  <tbody>\n    <tr ng-repeat="row in rows track by $index">\n      <td ng-repeat="dt in row track by dt.date" class="text-center" role="gridcell" id="{{dt.uid}}" aria-disabled="{{!!dt.disabled}}">\n        <button type="button" style="width:100%;" class="btn btn-default" ng-class="{\'btn-info\': dt.selected, active: isActive(dt)}" ng-click="select(dt.date)" ng-disabled="dt.disabled" tabindex="-1"><span ng-class="{\'text-info\': dt.current}">{{dt.label}}</span></button>\n      </td>\n    </tr>\n  </tbody>\n</table>\n';
    });
  });

  /* jshint unused: false */
  it('should match the retreived month picker template and sample html markup', inject(function () {
    expect(template).toBe(htmlelement);
  }));

  it('should validate monthpicker decorated property', function () {
    expect(templateUrl).toBe('calendar/monthpicker.decorator.html');
  });
});
