(function () {
  'use strict';

  angular
    .module('fis.bootstrap.dialog')
    .directive('fisBsDialogBody', dialogBody);

  function dialogBody() {
    return {
      restrict: 'E',
      transclude: true,
      scope: true,
      require: '^fisBsDialog', /* just for structure validation. We dont use the controller.*/
      templateUrl: 'dialog/dialogBody.directive.html'
    };
  }
})();

