// define a mock translate filter
angular.module('fis.bootstrap.components').filter('translate', function () {
  var filter = function (input) {
    filter.calls.push(input);
    return input;
  };

  filter.calls = [];
  filter.clear = function () {
    filter.calls = [];
  };

  return filter;
});

describe('Module: fisBootstrapComponents', function () {
  'use strict';

  beforeEach(function () {
    module('fis.bootstrap.components');
  });

  it('should have loaded the angular module', function () {
    inject(function () {
      expect(true).toEqual(true);
    });
  });
});
