(function () {
  'use strict';

  angular
    .module('fis.bootstrap.notification')
    .directive('fisBsNotification', notification);

  function notification() {
    'use strict';
    return {
      restrict: 'E',
      replace: true,
      scope: {
        fisBsNotificationsList: '=',
        fisBsShowStdErr: '=',
        fisBsStdErrMessage: '=?',
        fisBsForm: '='
      },
      templateUrl: 'notification/notification.directive.html'
    };
  }
})();

