'use strict';
var fs = require('fs');
var path = require('path');
var multiline = require('multiline');
var _ = require('lodash');

var appName = 'FIS Bootstrap Table';
var docPath = 'doc';

var pageTmplStr = multiline(function () { /*
<!DOCTYPE html>
<html>
  <title>${appName}</title>
  <xmp theme="journal" style="display:none;">
  ${markdown}
  </xmp>
  <script src="http://strapdownjs.com/v/0.2/strapdown.js"></script>
</html>
*/
});

var pageTmpl = _.template(pageTmplStr);

module.exports = function (req, res, next) {
  if (req.path.indexOf('/' + docPath) === 0) { // doc request
    // split the request path. The array would look like ['', 'doc', 'login', 'rest.service.md']
    var parts = req.path.split('/');
    if (parts.length === 2) { // base path. serve index.md
      fs.readFile(path.join(process.cwd(), parts[1], 'index.md'), 'utf-8', function (err, data) {
        if (err) throw err;
        res.send(getPage(data));
      });
    } else { // read the requested file and serve
      fs.readFile(path.join(process.cwd(), parts[1], parts[2], parts[3]), 'utf-8', function (err, data) {
        if (err) throw err;
        res.send(getPage(data));
      });
    }
  } else {
    next();
  }
};

function getPage(markdown) {
  return pageTmpl({ appName: appName, markdown: markdown });
}
