(function () {
  'use strict';

  angular
    .module('fis.bootstrap.table')
    .directive('stRatio', columnRatio);

  function columnRatio() {
    'use strict';

    return {
      link: function (scope, element, attr) {
        var ratio = attr.stRatio;

        element.css('width', ratio + '%');
      }
    };
  }
})();

