import angular from 'angular';

import data from './data.directive';
import dataVar from './data-var.directive';

import resourceService from './resource-service.provider';
import criteriaService from './criteria-service.provider';
import constraintsFactory from './constraints-factory.provider';

import typeBasedConstraints from './type-based-constraints.value';

const ngModule = angular.module('fis.core.data', []);

ngModule.directive('fisData', data);
ngModule.directive('fisDataVar', dataVar);

ngModule.provider('fisResourceService', resourceService);
ngModule.provider('fisCriteriaService', criteriaService);
ngModule.provider('fisConstraintsFactory', constraintsFactory);

ngModule.value('typeBasedConstraints', typeBasedConstraints);

export default ngModule.name;
