import angular from 'angular';

// Utilities for component/directive authoring

import elementUtils from './element-utils.factory';
import fieldDirective from './field-directive-factory.factory';
import scopeUtils from './scope-utils.factory';
import uniqueIdService from './unique-id-service.factory';

import input from './input.directive';
import applyClassDirective from './apply-class-directive.directive';
import triggerFactory from './trigger-directives.directive';
import uniqueId from './unique-id.directive';

const ngModule = angular.module('fis.core.directives.utils', []);

ngModule.factory('fisElementUtils', elementUtils);
ngModule.factory('fisFieldDirectiveFactory', fieldDirective);
ngModule.factory('fisScopeUtils', scopeUtils);
ngModule.factory('fisUniqueIdService', uniqueIdService);

ngModule.directive('fisInput', input);
ngModule.directive('fisApplyClassDirective', applyClassDirective);
ngModule.directive('fisUniqueId', uniqueId);

['fisReinit', 'fisRefresh'].forEach(directive => {
  ngModule.directive(directive, triggerFactory(directive));
});

export default ngModule.name;
