/**
 * @name uncamelcase
 * @description This filter allows converting a camelCased string to a more human readable string.
 * <pre>'accountName' | uncamelcase => 'Account Name'
 * 'innerHTML' | uncamelcase => 'Inner HTML'
 * $filter('uncamelcase')('anotherString') => 'Another String'</pre>
 * @param {String} text The text to uncamelcase
 */
export default function () {
  'use strict';

  return function (text) {
    if (text) {
      return text
        // insert a space between lower & upper
        .replace(/([a-z])([A-Z])/g, '$1 $2')
        // space before last upper in a sequence followed by lower
        .replace(/\b([A-Z]+)([A-Z])([a-z])/, '$1 $2$3')
        // uppercase the first character
        .replace(/^\w/, function (str) {
          return str.toUpperCase();
        });
    }
    return '';
  };
}
