/**
 * This attribute directive allows localizing screens on the client side.
 * @name fisI18n
 * @methodOf fis.core.module
 */
export default function () {
  return {
    restrict: 'A',
    link: function (scope, element, attrs) {
      if (!attrs.fisI18n) {
        throw new Error('Should provide a value for the fis-i18n attribute');
      }
      element.html(scope.i18n(attrs.fisI18n, element.html() || undefined));
    }
  };
}
