import angular from 'angular';

import translate from './translate.filter';
import i18n from './i18n.filter';
import i18nService from './i18n-service.provider';
import translateProvider from './translate.provider';
import i18nDirective from './i18n.directive';
import translateDirective from './translate.directive';

const ngModule = angular.module('fis.core.i18n', []);

ngModule.filter('translate', translate);
ngModule.filter('i18n', i18n);

ngModule.provider('fisI18nService', i18nService);
ngModule.provider('$translate', translateProvider);

ngModule.directive('fisI18n', i18nDirective);
ngModule.directive('translate', translateDirective);

export default ngModule.name;
