import angular from 'angular';

import exceptionHandler from './exception-handler.decorator';
import clientStorage from './client-storage-service.provider';
import conditionalLoaderService from './conditional-loader-service.factory';
import configService from './config-service.provider.js';
import httpErrorInterceptor from './http-error-interceptor.provider.js';
import templateCacheService from './template-cache-service.provider.js';
import userContextService from './user-context-service.provider.js';

import httpErrorInterceptorConfig from './http-error-interceptor.config.js';

const ngModule = angular.module('fis.core.services', []);

ngModule.decorator('$exceptionHandler', exceptionHandler);

ngModule.provider('fisClientStorageService', clientStorage);
ngModule.provider('fisConfigService', configService);
ngModule.provider('fisHttpErrorInterceptor', httpErrorInterceptor);
ngModule.provider('fisTemplateCacheService', templateCacheService);
ngModule.provider('fisUserContextService', userContextService);

ngModule.factory('fisConditionalLoaderService', conditionalLoaderService);

ngModule.config(httpErrorInterceptorConfig);

export default ngModule.name;
