describe('fisApplyClassDirective', function () {
  beforeEach(angular.mock.module('fis.core'));

  it('should add a directive that is restricted to a className', function () {
    angular.mock.inject(function ($compile, $rootScope) {
      $rootScope.directiveName = 'ng-form';
      var element = $compile('<div fis-apply-class-directive="directiveName"></div>')($rootScope);
      $rootScope.$apply();

      // If the fis-primary directive was successful, a .btn class should be added.
      expect(element.hasClass('ng-pristine')).toBe(true);
    });
  });
});
