'use strict';

describe('translate directive', function () {
  beforeEach(angular.mock.module('fis.core'));

  it('should invoke the dummy translate directive when key is provided as attribute', function () {
    angular.mock.inject(function ($compile, $rootScope) {
      var elem = $compile('<p translate="key"></p>')($rootScope);
      expect(elem.text()).toBe('key');
    });
  });

  it('should invoke the dummy translate directive when key is provided as tag content', function () {
    angular.mock.inject(function ($compile, $rootScope) {
      var elem = $compile('<p translate>key</p>')($rootScope);
      expect(elem.text()).toBe('key');
    });
  });
});
