'use strict';

describe('i18nFilter', function () {
  beforeEach(angular.mock.module('fis.core'));

  beforeEach(angular.mock.module(function (fisI18nServiceProvider) {
    fisI18nServiceProvider.setDefaultLocale('en');
  }));

  it('should return default value on translate', inject(function ($filter) {
    var i18n = $filter('i18n');
    expect(i18n('abc.xyz', 'Name')).toEqual('Name');
  }));

  it('should display key if it is not present in the bundle and default value is not provided', inject(function ($filter) {
    var i18n = $filter('i18n');
    expect(i18n('abc.xyz')).toEqual('abc.xyz');
  }));
});
