'use strict';

/**
 * Tests for fisTemplateCacheService.
 *
 */
describe('fisTemplateCacheService tests', function () {
  beforeEach(inject(function ($templateCache) {
    $templateCache.put('foo', 'bar');
  }));

  it('Should get a template from $templateCache', function () {
    angular.mock.inject(function (fisTemplateCacheService) {
      var template = fisTemplateCacheService.getTemplate('foo');
      expect(template).toEqual('bar');
    });
  });

  it('Should remove a template from $templateCache', function () {
    angular.mock.inject(function (fisTemplateCacheService) {
      var template = fisTemplateCacheService.getTemplate('foo');
      expect(template).toEqual('bar');

      fisTemplateCacheService.removeTemplate('foo');

      template = fisTemplateCacheService.getTemplate('foo');
      expect(template).toBeUndefined();
    });
  });

  it('Should add a template to $templateCache', function () {
    angular.mock.inject(function (fisTemplateCacheService) {
      var templateName = 'template1';
      var template = 'template';

      fisTemplateCacheService.addTemplate(templateName, template);

      expect(fisTemplateCacheService.getTemplate(templateName)).toEqual(template);
    });
  });
});
