'use strict';

describe('fisUserContextService', function () {
  beforeEach(angular.mock.module('fis.core'));

  it('should add a user object to user context', function () {
    angular.mock.inject(function ($window, fisUserContextService) {
      var user = { userId: 'someUser' };
      fisUserContextService.context(user);

      var userFromContext = fisUserContextService.context();
      expect(userFromContext.userId).toEqual('someUser');
    });
  });
});

