import angular from 'angular';
import ngAnimate from 'angular-animate';
import ngRoute from 'angular-route';

import config from './views.config';

import elementDirective from './element.directive';
import viewsDirective from './views.directive';

import viewPanelService from './view-panel-service.provider';
import viewService from './view-service.provider';
import navigationService from './navigation-service.provider';

const ngModule = angular.module('fis.core.views', [ngAnimate, ngRoute]);

ngModule.config(config);

ngModule.directive('fisElement', elementDirective);
ngModule.directive('fisViews', viewsDirective);

ngModule.provider('fisViewPanelService', viewPanelService);
ngModule.provider('fisViewService', viewService);
ngModule.provider('fisNavigationService', navigationService);

export default ngModule.name;
